a {
  color: var(--bw-alternate-blue);
  text-decoration: none;
  cursor: pointer;
  cursor: hand;
  transition: all 0.2s ease;
  white-space: nowrap;
  position: relative;
}
a:focus {
  outline: none;
}
a:active {
  color: var(--bw-blue);
}
a:after {
    content: "";
    background: var(--bw-alternate-blue);
    border-radius: 2em;
    height: 1px;
    width: 100%;
    position: absolute;
    bottom: -.1em;
    left: 0;
    transition: height .15s ease, width .15s ease, left .15s ease, opacity .15s ease .15s;
}
a:hover:after,
a:focus:after {
    height: calc(100% + .2em);
    width: calc(100% + .6em);
    left: -.3em;
    opacity: .125;
    transition: height .15s ease, width .15s ease, left .15s ease, opacity 0s ease;
}
a.anchor-external i {
