callouts.css

.tooltip{
    cursor: pointer;
  border-top-left-radius: 0px !important;
}
/* Tooltip text */
.tooltip__tooltiptext {
        visibility: hidden;
    max-width: 300px;
    background-color: #fff;
    color: inherit;
    padding: 10px 15px;
    border-radius: 3px;
    border: var(--bw-border);
    box-shadow: var(--shadow);
    margin-left: var(--m);
    /* Position the tooltip text - see examples below! */
    position: absolute;
    z-index: 1;
    margin-top: -10px;
}
.tooltip__tooltiptext {
    position: relative;
    border: var(--bw-border);
    white-space: nowrap;
}
.tooltip__tooltiptext:after, .tooltip__tooltiptext:before {
    right: 100%;
    top: 20px;
    border: solid transparent;
    content: " ";
    height: 0;
    width: 0;
    position: absolute;
    pointer-events: none;
}
/* Show the tooltip text when you mouse over the tooltip container */
.tooltip--hover:hover .tooltip__tooltiptext {
    visibility: visible;
}
.tooltip--visible{
    visibility: visible !important;
}
.tooltip--hover .tooltip__tooltiptext{
    z-index: 10;
}


callouts.js

$('.tooltip--click').click(function(){
    $('.tooltip--click .tooltip__tooltiptext').toggleClass('tooltip--visible');
});

