@import url('buttons.css');

body *, html *{
  -webkit-transition: all 0.3s;
  -moz-transition: all 0.3s;
  transition: all 0.3s;
}
.card{
  border-radius: var(--border-radius);
  height: auto;
  margin: var(--m);
  box-shadow: var(--shadow);
  vertical-align: top;
}
.card h2{
  font-weight: 500;
  font-size: 1.2em;
  text-transform: uppercase;
  margin: 0;
