.card-image, .card-details, .card-action{
  padding: var(--m);
}
.normal .inner:last-child{
  border-top: var(--bw-border);
}
.left-image{
    cursor: pointer;
}
.left-image *{
  float: left;
  width: 50%;
  -webkit-user-select: none;  /* Chrome all / Safari all */
  -moz-user-select: none;     /* Firefox all */
  -ms-user-select: none;      /* IE 10+ */
