    display: none;
}
.checkboxes input[type="checkbox"] + label {
    display: block;
    cursor: pointer;
    position: relative;
    padding: 0.2em 0 0.2em 2.1em;
    float: left;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.checkboxes input[type="checkbox"] + label:before {
    color: #fff;
    font-family: "Bandwidth";
    text-align: center;
    width: 1.3em;
    height: 1.3em;
    display: block;
    position: absolute;
    top: calc(.2em + 2px);
    left: 2px;
    z-index: 1;
}
.checkboxes input[type="checkbox"]:checked + label:before {
    content: "\f19f";
}
.checkboxes input[type="checkbox"] + label:after {
    content: "";
    background: #fff;
    border: var(--bw-db-border-thick);
    border-radius: .2em;
    width: 1.3em;
    height: 1.3em;
    display: block;
    position: absolute;
    top: .2em;
    left: 0;
    transition: all 0.2s ease;
}
.checkboxes input[type="checkbox"]:checked + label:after {
    background: var(--bw-dark-blue);
}
.checkboxes input[type="checkbox"]:active + label:after {
    border-color: var(--bw-alternate-blue);
}
.checkboxes input[type="checkbox"]:checked:active + label:after {
    background: var(--bw-alternate-blue);
}
.checkboxes input[type="checkbox"]:focus:not(:hover) + label:after {
    box-shadow: 0 0 0 var(--xs) var(--bw-light-blue);
}
.checkboxes input[type="checkbox"][disabled] + label {
    opacity: 0.5;
}
.checkboxes input[type="checkbox"][disabled] + label:before {
    opacity: 0.5;
}


