.radio-group *,
.radio-group *:after {
    transition: all 0.2s ease;
}
/* hide input */
.radio-group input[type="radio"]:empty {
    display: none;
}
/* basic label styles */
.radio-group input[type="radio"]:empty ~ label {
    border: var(--bw-border);
    margin-right: -1px;
    padding: var(--m);
    cursor: pointer;
    float: left;
    width: auto;
    position: relative;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
