.table-wrap {
    background: #fff;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: 5px 5px 0 0;
    overflow-x: auto;
    position: relative;
}
.shadow-r {
    box-shadow: inset -15px 0 10px -10px rgba(0,0,0,.2);
}
.shadow-l {
    box-shadow: inset 15px 0 10px -10px rgba(0,0,0,.2);
}
.shadow-rl {
    box-shadow: inset -15px 0 10px -10px rgba(0,0,0,.2), inset 15px 0 10px -10px rgba(0,0,0,.2);
}
.table-wrap table {
    width: 100%;
}
.table-wrap th {
    color: #fff;
